

import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, Image } from 'react-native';
import { Calendar } from 'react-native-calendars';
import moment from 'moment';

// Meal Data (You can expand this)
const mealSchedule = {
    '2025-03-25': { name: 'Rice & Curry', image: 'data:image/jpeg;base64,' },
    '2025-03-26': { name: 'Pasta & Salad', image: 'data:image/jpeg;base64,' },
    '2025-03-27': { name: 'Chicken Biryani', image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS8kcnaDG7fkNFV1nAdN4OZluOv-N0I73W4xA&s' },
    '2025-03-28': { name: 'Vegetable Sandwich', image: 'data:image/jpeg;base64,' },
};

// Default meal image
const defaultMealImage = 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRCaAlxLDYoOyyW_S-rCn3bhZcHI66WiplHVQ&s';

const MealsScreen = () => {
    const today = moment().format('YYYY-MM-DD'); // Get today's date formatted properly
    const [selectedDate, setSelectedDate] = useState(today);
    const [meal, setMeal] = useState(mealSchedule[today] || { name: 'No meals assigned', image: defaultMealImage });

    // Function to update meal when date is selected
    const updateMeal = (date) => {
        setMeal(mealSchedule[date] || { name: 'No meals assigned', image: defaultMealImage });
    };

    useEffect(() => {
        updateMeal(selectedDate);
    }, [selectedDate]);

    return (
        <View style={styles.container}>
            <Text style={styles.header}>ðŸ“… Meals Schedule</Text>



            {/* Calendar Component */}
            <View style={styles.calendarContainer}>
                <Calendar
                    current={selectedDate}
                    onDayPress={(day) => setSelectedDate(day.dateString)}
                    markedDates={{
                        [selectedDate]: { selected: true, selectedColor: '#34C759' },
                        [today]: { selected: true, selectedColor: '#007AFF' }
                    }}
                    renderHeader={(date) => (
                        <Text style={styles.calendarHeader}>
                            {moment(date.toString()).format('MMMM YYYY')}
                        </Text>
                    )}

                    theme={{
                        todayTextColor: '#007AFF',
                        selectedDayBackgroundColor: '#34C759',
                        selectedDayTextColor: '#FFF',
                        arrowColor: '#FF9500',
                        textSectionTitleColor: '#000',
                        dayTextColor: '#000',
                        textDisabledColor: '#A0A0A0',
                        monthTextColor: '#333',
                    }}
                    style={styles.calendar}
                />

            </View>
            {/* Display Selected Date */}
            <View style={styles.selectedDateContainer}>
                <Text style={styles.selectedDateLabel}>
                    Selected Date: <Text style={styles.selectedDate}>{moment(selectedDate).format('ddd, DD MMM')}</Text>
                </Text>
            </View>



            {/* Meal Details */}
            <View style={styles.mealContainer}>
                <Image source={{ uri: meal.image }} style={styles.mealImage} />
                <Text style={styles.mealText}>{meal.name}</Text>
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
        backgroundColor: '#F2F2F7',
        paddingTop: 12,
    },
    header: {
        fontSize: 20,
        fontWeight: 'bold',
        marginBottom: 10,
        color: '#000',
    },
    selectedDate: {
        fontSize: 16,
        fontWeight: 'bold',
        color: '#333',
        marginBottom: 8,
    },
    calendarContainer: {
        width: '80%', marginTop: 10,
        backgroundColor: '#FFF',
        borderRadius: 15,
        padding: 7,
        shadowColor: '#000',
        shadowOpacity: 0.1,
        shadowRadius: 5,
        elevation: 3,
    },
    calendar: {
        borderRadius: 10,
    },
    mealContainer: {
        alignItems: 'center',
        marginTop: 15,
        padding: 12,
        backgroundColor: '#FFF',
        borderRadius: 12,
        shadowColor: '#000',
        shadowOpacity: 0.1,
        shadowRadius: 5,
        elevation: 3,
        width: '95%',
    },
    mealImage: {
        width: 120,
        height: 120,
        borderRadius: 10,
        marginBottom: 10,
    },
    mealText: {
        fontSize: 15,
        fontWeight: '500',
        color: '#333',
    },
    selectedDateContainer: {
        marginTop: 15,
        alignItems: 'center',
    },
    selectedDateLabel: {
        fontSize: 16,
        fontWeight: 'bold',
        color: '#000',
    },
    selectedDate: {
        fontSize: 16,
        fontWeight: 'bold',
        color: '#007AFF',
    },

});

export default MealsScreen;


